contracts/base/CRToken.sol.content:
// / SPDX-License-Identifier: MIT
pragma solidity =0.8.18;
import "../libraries/Errors.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

contract CRTokenUpgradeable is ERC20Upgradeable, OwnableUpgradeable {
    uint8 internal _decimal;

    /*
     * @notice mint will "mint" CRtoken for the staker's.
     * @param  to_ address of the staker's.
     * @param  amount_ amount of token that staker's stake's.
     */
    function mint(address account_, uint256 amount_) external onlyOwner {
        _mint(account_, amount_);
    }

    /**
     * @notice  returns tokens decimals
     */
    function decimals() public view virtual override returns (uint8) {
        return _decimal;
    }

    /**
     * @notice Initializes the contract with immutable variables.
     * @param name_ is the token name.
     * @param symbol_ is the token symbol.
     * @param decimal_ is the token decimals.
     */
    function initialize(
        string memory name_,
        string memory symbol_,
        uint8 decimal_
    ) external initializer {
        if (bytes(name_).length == 0) revert EmptyName();
        if (bytes(symbol_).length == 0) revert EmptySymbol();
        if (decimal_ == 0) revert ZeroDecimal();
        __ERC20_init(name_, symbol_);
        __Ownable_init();
        _decimal = decimal_;
    }

    /**
     * @notice burn will burn the cr token's
     * @param account_  address to burn token.
     * @param amount_ is the amount of `token` to burn.
     */
    function burn(address account_, uint256 amount_) external onlyOwner {
        _burn(account_, amount_);
    }
}

contracts/base/CruizeVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.18;
import "./getters/Getters.sol";
import "../helper/Helper.sol";
import "./setters/Setters.sol";
import "../interfaces/ICRERC20.sol";
import "../libraries/SharesMath.sol";
import "../module/zodiac/contracts/core/Module.sol";
import "../module/pausable/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../module/reentrancyGuard/ReentrancyGuardUpgradeable.sol";

abstract contract CruizeVault is
    Setters,
    Helper,
    PausableUpgradeable,
    ReentrancyGuardUpgradeable,
    Module
{
    using SafeMath for uint256;
    using SafeCast for uint256;
    using SafeMath for uint128;
    using ShareMath for Types.DepositReceipt;
    using SafeERC20 for IERC20;

    receive() external payable {
        revert();
    }

    function _updateDepositInfo(address _token, uint256 _amount) internal {
        Types.VaultState storage vaultState = vaults[_token];
        uint16 currentRound = vaultState.round;
        uint256 decimal = decimalsOf(_token);

        Types.DepositReceipt memory depositReceipt = depositReceipts[
            msg.sender
        ][_token];
        // If we have an unprocessed pending deposit from the previous rounds, we have to process it.
        uint256 unredeemedShares = depositReceipt.getSharesFromReceipt(
            currentRound,
            roundPricePerShare[_token][depositReceipt.round],
            decimal
        );

        uint256 depositAmount = _amount;
        // If we have a pending deposit in the current round, we add on to the pending deposit.
        if (currentRound == depositReceipt.round) {
            uint256 newAmount = uint256(depositReceipt.amount).add(_amount);
            depositAmount = newAmount;
        }
        uint256 totalDeposit = uint256(depositReceipt.totalDeposit).add(
            _amount
        );
        depositReceipts[msg.sender][_token] = Types.DepositReceipt({
            round: currentRound,
            amount: uint104(depositAmount),
            unredeemedShares: uint128(unredeemedShares),
            totalDeposit: uint104(totalDeposit)
        });

        uint256 newTotalPending = uint256(vaultState.totalPending).add(_amount);
        ShareMath.assertUint128(newTotalPending);

        vaultState.totalPending = uint128(newTotalPending);
    }

    /**
     *
     * @notice Withdraws the assets on the vault using the outstanding `DepositReceipt.amount`.
     * @param _amount is the amount to withdraw.
     * @param _token withdrawal token address.
     */
    function _instantWithdrawal(address _token, uint104 _amount) internal {
        Types.DepositReceipt storage depositReceipt = depositReceipts[
            msg.sender
        ][_token];
        Types.VaultState storage vaultState = vaults[_token];
        uint16 currentRound = vaultState.round;
        uint16 depositRound = depositReceipt.round;
        if (depositRound != currentRound)
            revert InvalidWithdrawalRound(depositRound, currentRound);
        uint256 receiptAmount = depositReceipt.amount;
        if (_amount > receiptAmount)
            revert NotEnoughWithdrawalBalance(receiptAmount, _amount);
        ShareMath.assertUint104(_amount);

        depositReceipt.amount = uint104(receiptAmount.sub(_amount));
        depositReceipt.totalDeposit = depositReceipt.totalDeposit - _amount;
        vaultState.totalPending = uint128(
            uint256(vaultState.totalPending).sub(_amount)
        );
        _transferHelper(_token, msg.sender, uint256(_amount));
    }

    /**
     * @notice Initiates a withdrawal that can be processed once the round completes.
     * @param _shares is the number of shares to withdraw.
     * @param _token is the address of withdrawal `asset`.
     */
    function _initiateStandardWithdrawal(
        address _token,
        uint256 _shares
    ) internal {
        uint16 currentRound = vaults[_token].round;
        Types.DepositReceipt memory depositReceipt = depositReceipts[
            msg.sender
        ][_token];
        uint256 unredeemedShares = depositReceipt.getSharesFromReceipt(
            currentRound,
            roundPricePerShare[_token][depositReceipt.round],
            decimalsOf(_token)
        );
        // If we have a pending deposit in the current round.
        // We do a max redeem before initiating a withdrawal.
        // But we check if they must first have unredeemed shares.
        if (_shares > unredeemedShares)
            revert NotEnoughWithdrawalShare(unredeemedShares, _shares);

        if (depositReceipt.amount > 0 || depositReceipt.unredeemedShares > 0) {
            _redeemShares(
                _token,
                depositReceipt,
                _shares,
                unredeemedShares,
                currentRound
            );
        }
        Types.Withdrawal storage withdrawal = withdrawals[msg.sender][_token];
        bool withdrawalIsSameRound = withdrawal.round == currentRound;
        uint256 existingShares = uint256(withdrawal.shares);
        uint256 withdrawalShares;
        if (withdrawalIsSameRound) {
            withdrawalShares = existingShares.add(_shares);
        } else {
            if (existingShares > 0)
                revert WithdrawalAlreadyExists(existingShares);
            withdrawalShares = _shares;
            withdrawal.round = currentRound;
        }

        ShareMath.assertUint128(withdrawalShares);
        withdrawal.shares = uint128(withdrawalShares);

        currentQueuedWithdrawalShares[_token] = uint256(
            currentQueuedWithdrawalShares[_token]
        ).add(_shares).toUint128();
    }

    /**
     * @notice Completes a scheduled withdrawal from a past round.
     * @param _token withdrawal token address.
     */
    function _completeStandardWithdrawal(address _token) internal {
        Types.Withdrawal storage withdrawal = withdrawals[msg.sender][_token];
        Types.VaultState storage vaultState = vaults[_token];
        uint256 withdrawalShares = withdrawal.shares;
        uint16 withdrawalRound = withdrawal.round;
        uint256 decimal = decimalsOf(_token);
        uint16 currentRound = vaults[_token].round;

        if (withdrawalShares == 0) revert ZeroWithdrawalShare();
        if (withdrawalRound == currentRound)
            revert InvalidWithdrawalRound(withdrawalRound, currentRound);

        withdrawal.shares = 0;
        vaultState.queuedWithdrawShares = uint128(
            uint256(vaultState.queuedWithdrawShares).sub(withdrawalShares)
        );
        uint256 withdrawAmount = ShareMath.sharesToAsset(
            withdrawalShares,
            roundPricePerShare[_token][withdrawalRound],
            decimal
        );
        Types.DepositReceipt storage depositReceipt = depositReceipts[
            msg.sender
        ][_token];
        ShareMath.assertUint104(withdrawAmount);

        if (withdrawAmount > depositReceipt.totalDeposit) {
            depositReceipt.totalDeposit = 0;
        } else {
            depositReceipt.totalDeposit =
                depositReceipt.totalDeposit -
                uint104(withdrawAmount);
        }

        lastQueuedWithdrawAmounts[_token] = lastQueuedWithdrawAmounts[_token]
            .sub(withdrawAmount);
        ICRERC20(cruizeTokens[_token]).burn(msg.sender, withdrawalShares);
        _transferHelper(_token, msg.sender, uint256(withdrawAmount));
        emit StandardWithdrawal(msg.sender,withdrawAmount,_token);
    }

    /************************************************
     *  VAULT OPERATIONS
     ***********************************************/

    /**
     * @notice Helper function that performs most administrative tasks
     * such as setting next strategy, minting new shares, getting vault fees, etc.
     * @param lastQueuedWithdrawAmount is old queued withdraw amount.
     * @param currentQueuedWithdrawShares is the queued withdraw shares for the current round.
     * @param token  is the `asset` for which round will close.
     * @return lockedBalance is the new balance used to calculate next strategy purchase size or collateral size
     * @return queuedWithdrawAmount is the new queued withdraw amount for this round.
     */
    function _closeRound(
        address token,
        uint256 lastQueuedWithdrawAmount,
        uint256 currentQueuedWithdrawShares,
        uint256 totalTokenBalance
    ) internal returns (uint256 lockedBalance, uint256 queuedWithdrawAmount) {
        uint256 mintShares;
        uint256 totalVaultFee;
        Types.VaultState storage vaultState = vaults[token];

        uint256 newPricePerShare;
        (
            lockedBalance,
            queuedWithdrawAmount,
            newPricePerShare,
            mintShares,
            totalVaultFee
        ) = calculateSharePrice(
            token,
            Types.CloseParams(
                decimalsOf(token),
                totalBalance(token,totalTokenBalance),
                totalSupply(token),
                lastQueuedWithdrawAmount,
                currentQueuedWithdrawShares
            )
        );

        // Finalize the pricePerShare at the end of the round
        uint16 currentRound = vaultState.round;
        roundPricePerShare[token][currentRound] = newPricePerShare;
        vaultState.totalPending = 0;
        vaultState.round = uint16(currentRound + 1);
        roundPricePerShare[token][vaultState.round] = 1;
        ICRERC20(cruizeTokens[token]).mint(address(this), mintShares);
        if (totalVaultFee > 0) {
            _transferHelper(token, payable(feeRecipient), totalVaultFee);
            emit CollectVaultFee(token, totalVaultFee);
        }
        emit CloseRound(token, currentRound, newPricePerShare, lockedBalance);
        return (lockedBalance, queuedWithdrawAmount);
    }

    /**
     * @notice Main function to make delegatecall on Gnosis Safe to transfer either an
     * ETH transfer or ERC20 transfer.
     * @param _paymentToken withdrawal token address.
     * @param _receiver is the receiving address.
     * @param _amount the transfer amount
     */
    function transferFromSafe(
        address _paymentToken,
        address _receiver,
        address _cruizeProxy,
        uint256 _amount
    )
        external
        onlyModule(_cruizeProxy)
        addressIsValid(_paymentToken)
        addressIsValid(_receiver)
        nonReentrant
    {
        if (_paymentToken == ETH) {
            (bool sent, ) = _receiver.call{value: _amount}("");
            if (!sent) revert FailedToTransferETH();
        } else {
            IERC20(_paymentToken).safeTransfer(_receiver, _amount);
        }
        emit TransferFromSafe(_receiver, _amount,_paymentToken);
    }
}

contracts/base/getters/Getters.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.18;
import "../../modifiers/Modifiers.sol";
import "../../module/ownable/OwnableUpgradeable.sol";
import "../../libraries/SharesMath.sol";
import "../../interfaces/ICRERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
abstract contract Getters is Modifiers, OwnableUpgradeable {
    using SafeMath for uint256;
    using ShareMath for Types.DepositReceipt;

    /************************************************
     *  GETTERS
     ***********************************************/
    /**
     * @notice Calculates the  management fee for this week's round.
     * @param token is the `asset` for which the fee will calculate.
     * @param currentBalance is total  value locked in valut.
     * @param pendingAmount is the pending deposit amount.
     * @param lastLockedAmount is the amount of funds locked from the previous round
     * @return vaultFee is the total vault Fee.
     */
    function getVaultFees(
        address token,
        uint256 currentBalance,
        uint256 pendingAmount,
        uint256 lastLockedAmount
    ) internal view returns (uint256 vaultFee) {
        uint256 lockedBalance = currentBalance > pendingAmount
            ? currentBalance.sub(pendingAmount)
            : 0;
        //slither-disable-next-line uninitialized-local
        uint256 _performanceFeeInAsset;
        //slither-disable-next-line uninitialized-local
        uint256 _managementFeeInAsset;
        uint256 decimal = 10 ** decimalsOf(token);

        if (lockedBalance > lastLockedAmount) {
            // performance fee will be applied on round APY
            if (isPerformanceFeeEnabled) {
                _performanceFeeInAsset = performanceFee > 0
                    ? getPerformanceFee(
                        lockedBalance.sub(lastLockedAmount),
                        decimal
                    )
                    : 0;
            }

            // management fee will be applied on locked amount
            if (isManagementFeeEnable) {
                _managementFeeInAsset = managementFee > 0
                    ? getManagementFee(lockedBalance, decimal)
                    : 0;
            }
        }
        vaultFee = _performanceFeeInAsset.add(_managementFeeInAsset);
    }

    /**
     * @notice Getter for returning the account's share balance split between account and vault holdings
     * @param account is the account to lookup share balance for.
     * @param token is the `asset` address for which shares are calculating.
     * @return heldByAccount is the shares held by account.
     * @return heldByVault is the shares held on the vault (unredeemedShares).
     */
    function shareBalances(
        address token,
        address account
    )
        public
        view
        tokenIsAllowed(token)
        addressIsValid(account)
        returns (
            uint256 heldByAccount,
            uint256 heldByVault,
            uint256 totalShares
        )
    {
        Types.DepositReceipt memory depositReceipt = depositReceipts[account][
            token
        ];

        if (depositReceipt.round < ShareMath.PLACEHOLDER_UINT) {
            return (balanceOf(token, account), 0, 0);
        }

        uint256 unredeemedShares = depositReceipt.getSharesFromReceipt(
            vaults[token].round,
            priceOfRound(token, depositReceipt.round),
            decimalsOf(token)
        );
        return (
            balanceOf(token, account),
            unredeemedShares,
            balanceOf(token, account).add(unredeemedShares)
        );
    }

    /*
     * @notice Returns the user total locked amount in the strategy
     * @params token asset address
     * @params account user address
     */
    function balanceOfUser(
        address token,
        address account
    )
        external
        view
        tokenIsAllowed(token)
        addressIsValid(account)
        returns (uint256 balance)
    {
        Types.DepositReceipt memory depositReceipt = depositReceipts[account][
            token
        ];
        Types.VaultState memory vaultState = vaults[token];
        uint16 currentRound = vaultState.round;
        uint256 decimal = decimalsOf(token);

        // calculate total unredeemed shares of user's
        uint256 unredeemedShares = depositReceipt.getSharesFromReceipt( // 0
            currentRound,
            priceOfRound(token, depositReceipt.round),
            decimal 
        );

        uint256 currentAmount = ShareMath.sharesToAsset(
            unredeemedShares,
            priceOfRound(token, currentRound - 1),
            decimal
        );
        return balance.add(currentAmount);
    }

    /**
     * @notice Returns the vault's total balance, including the amounts locked into a strategy.
     * @return total balance of the vault, including the amounts locked in strategy.
     */
    function totalBalance(
        address token,
        uint256 tokenTotalBalance
    ) internal view returns (uint256) {
        if (tokenTotalBalance == 0) {
            if (token == ETH) return gnosisSafe.balance;
            else return IERC20(token).balanceOf(gnosisSafe);
        }
        return tokenTotalBalance;
    }

    /**
     * @notice Returns the `asset` total supply.
     * @return total supply of the asset.
     */
    function totalSupply(address token) internal view returns (uint256) {
        return IERC20(cruizeTokens[token]).totalSupply();
    }

    /**
     * @notice Returns the cr`token` balance.
     */
    function balanceOf(
        address token,
        address account
    )
        public
        view
        tokenIsAllowed(token)
        addressIsValid(account)
        returns (uint256)
    {
        return IERC20(cruizeTokens[token]).balanceOf(account);
    }

    /**
     * @notice Returns the vault round price per shares.
     */
    function priceOfRound(
        address token,
        uint16 round
    ) private view  returns (uint256) {
        if (roundPricePerShare[token][round] == 0) return 1e18;
        return roundPricePerShare[token][round];
    }

    /**
     * @notice Returns the vault managementFee.
     */
    function getManagementFee(
        uint256 totalBalanceLocked,
        uint256 decimal
    ) public view returns (uint256) {
        return calculateFee(totalBalanceLocked, decimal, managementFee);
    }

    /**
     * @notice Returns the vault performanceFee.
     */
    function getPerformanceFee(
        uint256 roundAPY,
        uint256 decimal
    ) public view returns (uint256) {
        return calculateFee(roundAPY, decimal, performanceFee);
    }

    /**
     * @notice Returns the fee.
     * @param amount is on which fee calculated.
     * @param decimal is token decimal.
     * @param feePercent is the parcentage of fee to charge.
     */
    function calculateFee(
        uint256 amount,
        uint256 decimal,
        uint256 feePercent
    ) internal pure returns (uint256 feeInAsset) {
        feeInAsset = feePercent
            .mul(decimal)
            .mul(amount)
            .div(FEE_MULTIPLIER)
            .div(100 * decimal);
    }

    function pricePerShare(address token) external  tokenIsAllowed(token) view returns (uint256) {
        uint256 totalTokenBalance = vaults[token].totalPending +
            vaults[token].lockedAmount;
        return
            ShareMath.pricePerShare(
                totalSupply(token),
                totalTokenBalance,
                vaults[token].totalPending,
                decimalsOf(token)
            );
    }

    /**
     * @notice Returns the vault total pending.
     */
    function totalTokenPending(
        address token
    ) external view tokenIsAllowed(token) returns (uint256) {
        return vaults[token].totalPending;
    }

    /**
     * @notice Returns the token decimals.
     */

    function decimalsOf(
        address _token
    ) internal view returns (uint256 decimal) {
        if (_token == ETH) decimal = 18;
        else decimal = ICRERC20(_token).decimals();
    }
}

contracts/base/setters/Setters.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.18;
import "../../libraries/SharesMath.sol";
import "../../libraries/Events.sol";
import "../getters/Getters.sol";
contract Setters is Events, Getters {
    using SafeMath for uint256;

    /************************************************
     *  SETTERS
     ***********************************************/
    /**
     * @notice Sets a new cap for deposits.
     * @param newCap is the new cap for deposits.
     * @param token is the address for which we have to set new cap.
     */
    function setCap(
        address token,
        uint256 newCap
    ) external onlyOwner tokenIsAllowed(token) numberIsNotZero(newCap) {
        uint256 currentTokenBalance = totalBalance(token, 0);
        if (newCap < currentTokenBalance)
            revert InvalidCap(currentTokenBalance, newCap);
        ShareMath.assertUint104(newCap);
        Types.VaultState storage vault = vaults[token];
        emit CapSet(token, vault.cap, newCap);
        vault.cap = uint104(newCap);
    }

    /**
     * @notice Sets the new newFeeRecipient_.
     * @param newFeeRecipient is the address of the new feeRecipient_.
     */
    function setFeeRecipient(
        address newFeeRecipient
    ) external onlyOwner addressIsValid(newFeeRecipient) {
        feeRecipient = newFeeRecipient;
    }

    /**
     * @notice Sets the management/performance fee status enable/disable.
     * @param _performanceFee is the performance fee status
     * @param _managementFee is the management fee status
     */
    function setFeeStatus(
        bool _performanceFee,
        bool _managementFee
    ) public onlyOwner {
        isPerformanceFeeEnabled = _performanceFee;
        isManagementFeeEnable = _managementFee;
    }

    /**
     * @notice Sets the management fee for the rounds.
     * @param newManagementFee is the management fee (18 decimals). ex: 2 * 10 ** 18 = 2%
     */
    function setManagementFee(
        uint256 newManagementFee
    ) public onlyOwner numberIsNotZero(newManagementFee) {
        if (newManagementFee > 100 * FEE_MULTIPLIER) revert InvalidFee();
        // We are dividing annualized management fee by num weeks in a year
        uint256 tmpManagementFee = newManagementFee.mul(FEE_MULTIPLIER).div(
            ROUND_PER_YEAR
        );
        emit ManagementFeeSet(managementFee, newManagementFee);
        managementFee = tmpManagementFee;
    }

    /**
     * @notice Sets the performance fee for the vault
     * @param newPerformanceFee is the performance fee (18 decimals). ex: 20 * 10 ** 18 = 20%
     */
    function setPerformanceFee(
        uint256 newPerformanceFee
    ) public numberIsNotZero(newPerformanceFee) onlyOwner {
        if (newPerformanceFee > 100 * FEE_MULTIPLIER) revert InvalidFee();
        emit PerformanceFeeSet(performanceFee, newPerformanceFee);
        performanceFee = newPerformanceFee;
    }

    /**
     * @notice Enable or Disable the asset status, so cruize can stop/resume token operations
     * @param token asset address
     * @param status enable/disable status of asset
     */
    function changeAssetStatus(
        address token,
        bool status
    ) public onlyOwner tokenIsAllowed(token) {
        isDisable[token] = status;
        emit ChangeAssetStatus(token, status);
    }

    /**
     * @param token - asset to delist from the contract.
     */
    function deListTokens(
        address token
    ) external onlyOwner tokenIsAllowed(token) {
        if (totalBalance(token, 0) > 0)
            revert TokenBalanceShouldBeZero(totalBalance(token, 0));

        uint256 tokenLength = tokens.length;
        address[] memory tokensList = new address[](tokenLength);
        for (uint256 i = 0; i < tokenLength; ) {
            if (tokens[i] != token) {
                tokensList[i] = tokens[i];
            }
            unchecked {
                i++;
            }
        }
        cruizeTokens[token] = address(0);
        delete vaults[token];
        tokens = tokensList;
        emit DeListToken(token);
    }

    function setRoundPerYear(
        uint256 NewRoundPerYear
    ) external numberIsNotZero(NewRoundPerYear) onlyOwner {
        emit SetRoundPerYear(ROUND_PER_YEAR, NewRoundPerYear);
        ROUND_PER_YEAR = NewRoundPerYear;
    }

    function setCruizeModule(
        address newCruizeModule
    ) external addressIsValid(newCruizeModule) onlyOwner {
        emit SetCruizeModule(module, newCruizeModule);
        module = newCruizeModule;
    }


}

contracts/Cruize.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.18;
import "./base/CruizeVault.sol";
import "./proxies/CloneProxy.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract Cruize is CruizeVault, Proxy {
    using SafeMath for uint256;
    using SafeCast for uint256;
    using SafeERC20 for IERC20;

    /************************************************
     *  CONSTRUCTOR & INITIALIZATION
     ***********************************************/
    /**
     * @notice Initializes the gnosis Module contract with storage variables.
     * @dev Initialize function, will be triggered when a new Cruize contract deployed.
     * @param initializeParams Parameters of initialization encoded.
     */

    function setUp(
        bytes memory initializeParams
    ) public virtual override initializer {
        __Ownable_init();
        __ReentrancyGuard_init();
        (
            address _owner,
            address _vault,
            address _crContract,
            address _cruizeProxy,
            address _logic,
            uint256 _managementFee,
            uint256 _performanceFee
        ) = abi.decode(
                initializeParams,
                (address, address, address, address, address, uint256, uint256)
            );
        gnosisSafe = _vault;
        crContract = _crContract;
        feeRecipient = _owner;
        cruizeProxy = _cruizeProxy;
        module = _logic;
        isPerformanceFeeEnabled = true;
        isManagementFeeEnable = true;
        setManagementFee(_managementFee);
        setPerformanceFee(_performanceFee);
        setAvatar(_owner);
        setTarget(_vault);
        transferOwnership(_owner);
    }

    /**
     * @notice createToken will Clone CRTokenUpgradeable (ERC20 token).
     * @param name name of crtoken  .
     * @param symbol symbol of crtoken .
     */
    function createToken(
        string memory name,
        string memory symbol,
        address token,
        uint104 tokenCap
    )
        external
        numberIsNotZero(tokenCap)
        addressIsValid(token)
        nonReentrant
        onlyOwner
    {
        uint8 decimal = uint8(decimalsOf(token));
        if (cruizeTokens[token] != address(0)) revert AssetAlreadyExists(token);
        ICRERC20 crToken = ICRERC20(createClone(crContract));
        cruizeTokens[token] = address(crToken);
        crToken.initialize(name, symbol, decimal);
        vaults[token].round = 1;
        vaults[token].cap = tokenCap;
        address[] storage allTokens = tokens;
        allTokens.push(token);
        tokens = allTokens;
        emit CreateToken(
            token,
            address(crToken),
            name,
            symbol,
            decimal,
            tokenCap
        );
    }

    /************************************************
     *  DEPOSIT & WITHDRAWALS
     ***********************************************/
    /**
     * @notice Deposits the `asset` from msg.sender
     * @param token depositing token address.
     * @param amount is the amount of `asset` to deposit.
     */
    function deposit(
        address token,
        uint256 amount
    )
        external
        payable
        nonReentrant
        tokenIsAllowed(token)
        numberIsNotZero(amount)
        isDisabled(token)
        whenNotPaused
    {
        _updateDepositInfo(token, amount);
        if (
            token == ETH && gnosisSafe.balance.add(amount) <= vaults[token].cap
        ) {
            // transfer ETH to Cruize gnosis valut.
            (bool sent, ) = gnosisSafe.call{value: amount}("");
            if (!sent) revert FailedToTransferETH();
        } else if (
            IERC20(token).balanceOf(gnosisSafe).add(amount) <= vaults[token].cap
        ) {
            // transfer token to Cruize gnosis vault.
            IERC20(token).safeTransferFrom(msg.sender, gnosisSafe, amount);
        } else {
            revert VaultReachedDepositLimit(vaults[token].cap);
        }
        emit Deposit(msg.sender, amount, token);
    }

    /************************************************
     *  VAULT OPERATIONS
     ***********************************************/
    /**
     * @notice Completes a scheduled withdrawal from a past round. Uses finalized pps for the round
     * @param token depositing token address.
     */
    function standardWithdrawal(
        address token
    )
        external
        nonReentrant
        tokenIsAllowed(token)
        isDisabled(token)
        whenNotPaused
    {
        _completeStandardWithdrawal(token);
    }

    /**
     * @notice Initiates a withdrawal that can be processed once the round completes
     * @param numShares is the number of shares to withdraw
     * @param token withdrawal `asset` address.
     */
    function initiateWithdrawal(
        address token,
        uint256 numShares
    )
        external
        nonReentrant
        tokenIsAllowed(token)
        numberIsNotZero(numShares)
        isDisabled(token)
        whenNotPaused
    {
        _initiateStandardWithdrawal(token, numShares);
        emit InitiateStandardWithdrawal(msg.sender, token, numShares);
    }

    /**
     * @notice Withdraws the assets on the vault using the outstanding `DepositReceipt.amount`
     * @param amount is the amount to withdraw.
     * @param token withdrawal `asset` address.
     */
    function instantWithdrawal(
        address token,
        uint256 amount
    )
        external
        nonReentrant
        tokenIsAllowed(token)
        numberIsNotZero(amount)
        isDisabled(token)
        whenNotPaused
    {
        ShareMath.assertUint104(amount);
        _instantWithdrawal(token, amount.toUint104());
        emit InstantWithdrawal(msg.sender, amount, vaults[token].round, token);
    }

    /**
     * @notice function closeRound  will be responsible for closing vault rounds.
     * @param totalTokensBalance  - array of token balances.
     */
    function closeTokensRound(
        address[] memory tokensList,
        uint256[] memory totalTokensBalance
    ) external nonReentrant onlyOwner {
        uint256 tokenLength = tokensList.length;
        if (tokenLength != totalTokensBalance.length)
            revert InvalidLength(tokenLength, totalTokensBalance.length);
        for (uint8 i = 0; i < tokenLength; i++) {
            address token = tokensList[i];
            uint256 tokenBalance = totalTokensBalance[i];
            if (!isDisable[token]) {
                uint256 vaultTokenBalance = totalBalance(token, tokenBalance);
                _closeRound(token, tokenBalance, vaultTokenBalance);
            }
        }
    }

    function _closeRound(
        address token,
        uint256 totalTokenBalance,
        uint256 vaultTokenBalance
    ) private tokenIsAllowed(token) {
        checkVaultBalance(token, totalTokenBalance, vaultTokenBalance);
        uint256 currQueuedWithdrawShares = currentQueuedWithdrawalShares[token];
        (uint256 lockedBalance, uint256 queuedWithdrawAmount) = _closeRound(
            token,
            uint256(lastQueuedWithdrawAmounts[token]),
            currQueuedWithdrawShares,
            totalTokenBalance
        );
        lastQueuedWithdrawAmounts[token] = queuedWithdrawAmount;
        Types.VaultState storage vaultState = vaults[token];
        uint256 newQueuedWithdrawShares = uint256(
            vaultState.queuedWithdrawShares
        ).add(currQueuedWithdrawShares);
        ShareMath.assertUint128(newQueuedWithdrawShares);
        vaultState.queuedWithdrawShares = uint128(newQueuedWithdrawShares);
        //slither-disable-next-line reentrancy-no-eth
        currentQueuedWithdrawalShares[token] = 0;
        ShareMath.assertUint104(lockedBalance);
        vaultState.lockedAmount = uint104(lockedBalance);
    }

    function tokensTvl(
        address[] memory assets
    ) external view returns (uint256[][] memory) {
        uint256 length = assets.length;
        uint256[][] memory assetsCapAndTvl = new uint256[][](length);
        for (uint256 i = 0; i < length; i++) {
            address token = assets[i];
            uint256[] memory assetsTvlAndCapInfo = new uint256[](2);
            assetsTvlAndCapInfo[0] =
                vaults[token].lockedAmount +
                vaults[token].totalPending;
            assetsTvlAndCapInfo[1] = vaults[token].cap;
            assetsCapAndTvl[i] = assetsTvlAndCapInfo;
        }

        return assetsCapAndTvl;
    }
}

