contracts/libraries/SharesMath.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.18;
import {SafeMath} from "@openzeppelin/contracts/utils/math/SafeMath.sol";
import {Types} from "./Types.sol";

library ShareMath {
    using SafeMath for uint256;

    uint256 internal constant PLACEHOLDER_UINT = 1;

    function assetToShares(
        uint256 assetAmount,
        uint256 assetPerShare,
        uint256 decimals
    ) internal pure returns (uint256) {
        // If this throws, it means that vault's roundPricePerShare[currentRound] has not been set yet
        // which should never happen.
        // Has to be larger than 1 because `1` is used in `initRoundPricePerShares` to prevent cold writes.
        require(assetPerShare > PLACEHOLDER_UINT, "Invalid assetPerShare");

        return assetAmount.mul(10**decimals).div(assetPerShare);
    }

    function sharesToAsset(
        uint256 shares,
        uint256 assetPerShare,
        uint256 decimals
    ) internal pure returns (uint256) {
        // If this throws, it means that vault's roundPricePerShare[currentRound] has not been set yet
        // which should never happen.
        // Has to be larger than 1 because `1` is used in `initRoundPricePerShares` to prevent cold writes.
        require(assetPerShare > PLACEHOLDER_UINT, "Invalid assetPerShare");

        return shares.mul(assetPerShare).div(10**decimals);
    }

    /**
     * @notice Returns the shares unredeemed by the user given their DepositReceipt
     * @param depositReceipt is the user's deposit receipt
     * @param currentRound is the `round` stored on the vault
     * @param assetPerShare is the price in asset per share
     * @param decimals is the number of decimals the asset/shares use
     * @return unredeemedShares is the user's virtual balance of shares that are owed
     */
    function getSharesFromReceipt(
        Types.DepositReceipt memory depositReceipt,
        uint256 currentRound,
        uint256 assetPerShare,
        uint256 decimals
    ) internal pure returns (uint256 unredeemedShares) {
        if (depositReceipt.round > 0 && depositReceipt.round < currentRound) {
            uint256 sharesFromRound = assetToShares(
                depositReceipt.amount,
                assetPerShare,
                decimals
            );
            return
                uint256(depositReceipt.unredeemedShares).add(sharesFromRound);
        }
        return depositReceipt.unredeemedShares;
    }

    function pricePerShare(
        uint256 totalSupply,
        uint256 totalBalance,
        uint256 pendingAmount,
        uint256 decimals
    ) internal pure returns (uint256) {
        uint256 singleShare = 10**decimals;
        return
            totalSupply > 0
                ? singleShare.mul(totalBalance.sub(pendingAmount)).div(
                    totalSupply
                )
                : singleShare;
    }

    /************************************************
     *  HELPERS
     ***********************************************/

    function assertUint104(uint256 num) internal pure {
        require(num <= type(uint104).max, "Overflow uint104");
    }

    function assertUint128(uint256 num) internal pure {
        require(num <= type(uint128).max, "Overflow uint128");
    }
}

contracts/libraries/Types.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.18;

library Types {
    /************************************************
     *  IMMUTABLES & CONSTANTS
     ***********************************************/
    uint256 internal constant UNIT = 10**18;
    uint256 internal constant PLACEHOLDER_UINT = 1;

    struct VaultState {
        // 32 byte slot 1
        // Current round number. `round` represents the number of `period`s elapsed.
        uint16 round;
        // Amount that is currently locked for selling options
        uint104 lockedAmount;
        uint128 totalPending;
        // Total amount of queued withdrawal shares from previous rounds (doesn't include the current round)
        uint128 queuedWithdrawShares;
        //  Total amount that can be held in a vault.
        uint104 cap;
    }

    struct DepositReceipt {
        // Maximum of 65535 rounds. Assuming 1 round is 7 days, maximum is 1256 years.
        uint16 round;
        // Deposit amount, max 20,282,409,603,651 or 20 trillion ETH deposit
        uint104 amount;
        // Unredeemed shares balance
        uint128 unredeemedShares;
        //  total locked amount.
        uint104 totalDeposit;

    }

    struct Withdrawal {
        // Maximum of 65535 rounds. Assuming 1 round is 7 days, maximum is 1256 years.
        uint16 round;
        // Number of shares withdrawn
        uint128 shares;
    }

    struct CloseParams {
        // Decimals of  token
        uint256 decimals;
        // Total balance of token.
        uint256 totalBalance;
        //  Total share supply for an round.
        uint256 currentShareSupply;
        //  Total queued withdrawal from the last round.
        uint256 lastQueuedWithdrawAmount;
        //  Total  current round queued withdrawal.
        uint256 currentQueuedWithdrawShares;
    }
}

contracts/modifiers/Modifiers.sol.content:
// SPDX-License-Identifier: MIT
import "../libraries/Errors.sol";
import "../storage/CruizeStorage.sol";
pragma solidity =0.8.18;

abstract contract Modifiers is CruizeStorage {
    /************************************************
     * MODIFIERS
     ***********************************************/

    /**
     * @dev Throws if called by any account other than the module.
     */

    modifier onlyModule(address _cruizeProxy) {
        if (msg.sender != _cruizeProxy)
            revert NotAuthorized(msg.sender, _cruizeProxy);
        _;
    }

    /**
     * @dev Throws if cruizeTokens mapping give's null.
     */
    modifier tokenIsAllowed(address token) {
        if (cruizeTokens[token] == address(0)) revert AssetNotAllowed(token);
        _;
    }
    /**
     * @dev Throws if number is zero.
     */
    modifier numberIsNotZero(uint256 number) {
        if (number == 0) revert ZeroValue(number);
        _;
    }
    /**
     * @dev Throws if address is null.
     */
    modifier addressIsValid(address addr) {
        if (addr == address(0)) revert ZeroAddress(addr);
        _;
    }
/**
 * @dev Throws if token address is  disabled
 */
    modifier isDisabled(address token) {
        if (isDisable[token]) revert DisabledAsset(token);
        _;
    }

}

